from PIL import Image, ImageDraw
import face_recognition
import cv2


cam = cv2.VideoCapture(0)   # 0 -> index of camera
s, img = cam.read()

# Load the jpg file into a numpy array
image = img

# Find all facial features in all the faces in the image
face_landmarks_list = face_recognition.face_landmarks(image)

while True:
    for face_landmarks in face_landmarks_list:
        pil_image = Image.fromarray(image)
        d = ImageDraw.Draw(pil_image, 'RGBA')

        # Make the eyebrows into a nightmare
   
        d.line(face_landmarks['left_eyebrow'])
        d.line(face_landmarks['right_eyebrow'])

        # Gloss the lips
    
        d.line(face_landmarks['top_lip'])
        d.line(face_landmarks['bottom_lip'])

        # Sparkle the eyes
        d.polygon(face_landmarks['left_eye'])
        d.polygon(face_landmarks['right_eye'])

        # Apply some eyeliner
        d.line(face_landmarks['left_eye'] + [face_landmarks['left_eye'][0]])
        d.line(face_landmarks['right_eye'] + [face_landmarks['right_eye'][0]])

        d.line(face_landmarks['chin'])

        d.line(face_landmarks['chin'])
        d.line(face_landmarks['nose_tip'])
        d.line(face_landmarks['nose_bridge'])
        
    break
        
        
        
for th in face_landmarks:
    print(th)
pil_image.show()
        
    
